import { infoPokemon } from '../funtions/Funtions'


  
const Pokemon = (props) => {
  const [info, setInfo] = useState(null) 
    useEffect (() => { 
      const pokeball = infoPokemon(props.data.url)
      setInfo(Pokemon)
     
    }, )

  return (
    <>
    {info != null ? (

    <h1> {info.base_experience}</h1>
     ) : ('no hay personajes')}

    
    </>
  )
}

export default Pokemon
